import{_ as t,c as r,o as n,a2 as o}from"./chunks/framework.Bfq10Vlj.js";const a="/assets/2025-07.Db3wnYVs.png",p=JSON.parse('{"title":"July 2025","description":"","frontmatter":{"github_repo":"https://github.com/gardener/documentation","github_subdir":"website/community/hackathons","params":{"github_branch":"master"},"path_base_for_github_subdir":{"from":"content/community/hackathons/2025-07.md","to":"2025-07.md"},"title":"July 2025","weight":-202507,"prev":false,"next":false},"headers":[],"relativePath":"community/hackathons/2025-07/index.md","filePath":"community/hackathons/2025-07.md","lastUpdated":null}'),s={name:"community/hackathons/2025-07/index.md"};function i(l,e,d,c,h,g){return n(),r("div",null,e[0]||(e[0]=[o('<h1 id="hack-the-garden-sofia-edition-07-2025-wrap-up" tabindex="-1">Hack The Garden Sofia Edition 07/2025 Wrap Up <a class="header-anchor" href="#hack-the-garden-sofia-edition-07-2025-wrap-up" aria-label="Permalink to &quot;Hack The Garden Sofia Edition 07/2025 Wrap Up&quot;">â€‹</a></h1><ul><li>ğŸ—“ï¸ <strong>Date:</strong> 21.07.2025 â€“ 25.07.2025</li><li>ğŸ“ <strong>Location:</strong> <a href="https://maps.app.goo.gl/SPdvQ4F2p7Qqfx4p9" target="_blank" rel="noreferrer">SAP Center Sofia</a></li><li>ğŸ‘¤ <strong>Organizer:</strong> <a href="https://www.sap.com/" target="_blank" rel="noreferrer">SAP</a></li><li>ğŸ“˜ <strong>Topics:</strong> /</li></ul><p><img src="'+a+'" alt="Group picture"></p><h2 id="ğŸ›¡ï¸-viewer-kubeconfigs-expose-requestor-s-identity-potential-privilege-leak" tabindex="-1">ğŸ›¡ï¸ Viewer kubeconfigs expose requestor&#39;s identity (potential privilege leak) <a class="header-anchor" href="#ğŸ›¡ï¸-viewer-kubeconfigs-expose-requestor-s-identity-potential-privilege-leak" aria-label="Permalink to &quot;ğŸ›¡ï¸ Viewer kubeconfigs expose requestor&#39;s identity (potential privilege leak)&quot;">â€‹</a></h2><p><strong>Problem Statement:</strong> Viewer kubeconfigs currently include the requestor&#39;s username, which can unintentionally grant elevated privileges in the Shoot cluster. We should revisit how identities are issued and anonymize or restrict viewer credentials.</p><p><strong>Motivation/Benefits:</strong> ğŸ™… Prevent unintended privilege escalation from read-only permissions due to unwanted impersonation (username clash in different identities) ğŸ›¡ï¸ Use dedicate user groups to distinguish different personas (project vs system administrators and viewers)</p><p><strong>Achievements:</strong></p><ul><li>Avoid unwanted impersonation eventually causing privilege escalation by adding randomly generated prefix to the username in the kubeconfig&#39;s client certificate issued by the <code>shoots/adminkubeconfig</code> and <code>shoots/viewerkubeconfig</code> subresources.</li><li>Use dedicated groups for the different user roles: system administrator, project administrator, system viewer and project viewer.</li></ul><p><strong>Next Steps:</strong> N/A</p><p><strong>Code/Pull Requests:</strong><a href="https://github.com/gardener/gardener/pull/12597" target="_blank" rel="noreferrer">https://github.com/gardener/gardener/pull/12597</a><a href="https://github.com/gardener/gardener/pull/12673" target="_blank" rel="noreferrer">https://github.com/gardener/gardener/pull/12673</a><a href="https://github.com/gardener/gardener/pull/12674" target="_blank" rel="noreferrer">https://github.com/gardener/gardener/pull/12674</a></p><h2 id="ğŸš€-pvc-autoscaler" tabindex="-1">ğŸš€ PVC Autoscaler <a class="header-anchor" href="#ğŸš€-pvc-autoscaler" aria-label="Permalink to &quot;ğŸš€ PVC Autoscaler&quot;">â€‹</a></h2><p><strong>Problem Statement:</strong> Reiterate over the API of the existing pvc-autoscaler. Resolve the conceptual issue after reworking the pvc-autoscaler from annotations to CRDs - how can a PersistentVolumeAutoscaler resource track multiple PVCs (n replicas) of StatefulSet.</p><p><strong>Motivation/Benefits:</strong> ğŸš€ Integrate the pvc-autoscaler into Gardener to:</p><ul><li>handle the volume of the observability stack to ensure its minimum retention period of 14d</li><li>reduce the default initial size of observability stack volumes to reduce costs from short-lived clusters that don&#39;t use the whole storage of the observability volumes</li><li>offer pvc-autoscaler to Shoot owners</li></ul><p><strong>Achievements:</strong> The PersistentVolumeAutoscaler resource was added to track all PVCs mounted inside the pods belonging to a controller. For instance, users can now directly target a StatefulSet or even a Prometheus resource and all of their PVCs will be tracked and potentially scaled up.</p><p><strong>Next Steps:</strong></p><ul><li>Check if the pvc-autoscaler can scale down volume size using VolumeSnapshots.</li></ul><p><strong>Code/Pull Requests:</strong><a href="https://github.com/plkokanov/pvc-autoscaler/tree/hackathon/pvc-autoscaler-with-crd" target="_blank" rel="noreferrer">https://github.com/plkokanov/pvc-autoscaler/tree/hackathon/pvc-autoscaler-with-crd</a></p><h2 id="ğŸ”-gardener-node-agent-crash-loop-on-configuration-changes-issue-11025" tabindex="-1">ğŸ” gardener-node-agentÂ crash-loop on configuration changes (Issue <a href="https://github.com/gardener/gardener/issues/11025" target="_blank" rel="noreferrer">#11025</a>) <a class="header-anchor" href="#ğŸ”-gardener-node-agent-crash-loop-on-configuration-changes-issue-11025" aria-label="Permalink to &quot;ğŸ” gardener-node-agentÂ crash-loop on configuration changes (Issue [#11025](https://github.com/gardener/gardener/issues/11025))&quot;">â€‹</a></h2><p><strong>Problem Statement:</strong> If breaking changes are introduced to the <code>gardener-node-agent</code> configuration, the agent might crash-loop without recovery. Let&#39;s investigate how to detect and safely handle incompatible or missing configuration, and improve robustness during rollouts.</p><p><strong>Motivation/Benefits:</strong> ğŸ” Improve the resilience of <code>gardener-node-agent</code> during rollout scenarios by using the matching versioned config in order to prevent crash-loop.</p><p><strong>Achievements:</strong></p><ul><li>Introduce early configuration validation logic to detect and get valid configuration for current version of <code>gardener-node-agent</code>.</li><li>Add a fix for forward-compatibility issues, ensuring the <code>gardener-node-agent</code> does not crash when the configuration is updated but the binary for it is not updated to a compatible version.</li></ul><p><strong>Next Steps:</strong> Review the PR and address any additional feedback. Monitor behavior during rollout across landscapes.</p><p><strong>Issue:</strong><a href="https://github.com/gardener/gardener/issues/11025" target="_blank" rel="noreferrer">https://github.com/gardener/gardener/issues/11025</a></p><p><strong>Code/Pull Requests:</strong><a href="https://github.com/gardener/gardener/pull/12589" target="_blank" rel="noreferrer">https://github.com/gardener/gardener/pull/12589</a></p><h2 id="ğŸ§©-gardener-node-agent-can-t-create-drop-ins-for-systemd-symlinked-units-issue-11026" tabindex="-1">ğŸ§© <code>gardener-node-agent</code> can&#39;t create drop-ins for systemd symlinked units (Issue <a href="https://github.com/gardener/gardener/issues/11026" target="_blank" rel="noreferrer">#11026</a>) <a class="header-anchor" href="#ğŸ§©-gardener-node-agent-can-t-create-drop-ins-for-systemd-symlinked-units-issue-11026" aria-label="Permalink to &quot;ğŸ§© `gardener-node-agent` can&#39;t create drop-ins for systemd symlinked units (Issue [#11026](https://github.com/gardener/gardener/issues/11026))&quot;">â€‹</a></h2><p><strong>Problem Statement:</strong> The <code>gardener-node-agent</code> attempts to enable and restart <code>systemd</code> units when configuration changes are applied. However, if a unit is referenced via an alias or symlink (e.g., sshd.service â†’ ssh.service), attempting to enable it results in a failure with the following error:</p><div class="language- vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span>Failed to enable unit: Refusing to operate on alias name or linked unit file: sshd.service</span></span></code></pre></div><p>This error prevents the OSC to be successfully applied.</p><p><strong>Motivation/Benefits:</strong> Improving compatibility with aliased or symlinked units enhances the robustness of <code>gardener-node-agent</code>. By avoiding failures during drop-in-based updates, configuration changes can be applied more reliably and consistently.</p><p><strong>Achievements:</strong></p><ul><li>Gain deeper understanding of <code>systemd</code> mechanics: <ul><li>difference between enabling a unit via <code>systemctl</code> and by manually creating symlinks</li><li>how configuration files for units are prioritized and loaded from different &quot;well known&quot; systemd locations</li><li>the logic behind unit aliases and the results of their explicit enabling</li></ul></li><li>Monitor behavior of drop-in config files without re-enabling the unit itself.</li><li>Validate in local setup that <code>systemd</code> would apply updated drop-ins after <code>systemctl daemon-reload</code> and a restart of the unit, without requiring a <code>systemctl enable</code> of the unit.</li></ul><p><strong>Next Steps:</strong> Finalize the implementation by skipping the enabling for units known to be aliases or symlinks.</p><p><strong>Issue:</strong><a href="https://github.com/gardener/gardener/issues/11026" target="_blank" rel="noreferrer">https://github.com/gardener/gardener/issues/11026</a></p><p><strong>Code/Pull Requests:</strong> A draft implementation exists that skips enabling symlinked units. The approach and its continuation is still under discussion within the development team.</p><h2 id="ğŸ›¡ï¸-internal-penetration-testing" tabindex="-1">ğŸ›¡ï¸ Internal Penetration Testing <a class="header-anchor" href="#ğŸ›¡ï¸-internal-penetration-testing" aria-label="Permalink to &quot;ğŸ›¡ï¸ Internal Penetration Testing&quot;">â€‹</a></h2><p><strong>Problem Statement:</strong> Let&#39;s try to do find new exploits and vulnerabilities in our system.</p><ul><li>Can we escalate privileges somewhere?</li><li>Can we inject a faulty field somewhere (code injection)?</li><li>Can we bypass a validation by a webhook somewhere?</li></ul><p><strong>Motivation/Benefits:</strong> ğŸ•µ Discover any potential security vulnerabilities in our systems</p><p><strong>Achievements:</strong> The team got familiar with the recently disclosed CVEs: <a href="https://github.com/gardener/gardener/issues/12136" target="_blank" rel="noreferrer">CVE-2025-47284</a>, <a href="https://github.com/gardener/gardener/issues/12137" target="_blank" rel="noreferrer">CVE-2025-47283</a>, <a href="https://github.com/gardener/external-dns-management/issues/462" target="_blank" rel="noreferrer">CVE-2025-47282</a>. Check if referenced resources can be attack-vector and are vulnerable to exploits. Go through existing annotations and check if something malicious can be injected via them.</p><p><strong>Next Steps:</strong> N/A</p><p><strong>Issue:</strong> N/A</p><p><strong>Code/Pull Requests:</strong> N/A</p><h2 id="ğŸ›¡ï¸-input-validation" tabindex="-1">ğŸ›¡ï¸ Input Validation <a class="header-anchor" href="#ğŸ›¡ï¸-input-validation" aria-label="Permalink to &quot;ğŸ›¡ï¸ Input Validation&quot;">â€‹</a></h2><p><strong>Problem Statement:</strong> We don&#39;t have a guide for developers how to write good input validation. Let&#39;s document what different types of validation we have across the components; how to write good validation code with examples; checklist and best practices. When we have the guide, we could even work on adapting components and codebase to comply with it.</p><p>Input validation is a vast area. There are several points for improvements we can make there. There is a need for a guide for developers how to write validation code - examples, checklist and best practices. On the other side, there are also many challenging topics in the input validation area. One of these topics is how can extension admission components validate referenced resources in easy and reliable manner.</p><p><strong>Motivation/Benefits:</strong> ğŸ”’ Improved security by validating referenced resources which were not validated by all extensions so far ensure that extensions have the needed framework to validate referenced resources.</p><p><strong>Achievements:</strong> We worked on the validation of referenced resources topic. We made an API change proposal in [<a href="https://github.com/gardener/gardener/issues/12582" target="_blank" rel="noreferrer">https://github.com/gardener/gardener/issues/12582</a>](Maintain an extension-specific label on referenced resources).</p><p><strong>Next Steps:</strong> There was the concern that the proposed solution could not cover very natively extensions with multiple referenced resources of the same kind. For example the registry-cache extension to have references for a TLS Secret and a DNS Secret. The PoC changes have to be made PR ready - add tests, documentation.</p><p><strong>Issue:</strong><a href="https://github.com/gardener/gardener/issues/12582" target="_blank" rel="noreferrer">https://github.com/gardener/gardener/issues/12582</a></p><p><strong>Code/Pull Requests:</strong></p><ul><li><a href="https://github.com/ialidzhikov/gardener/tree/enh/extension-resources-3" target="_blank" rel="noreferrer">https://github.com/ialidzhikov/gardener/tree/enh/extension-resources-3</a></li><li><a href="https://github.com/ialidzhikov/gardener-extension-registry-cache/tree/enh/extension-resources" target="_blank" rel="noreferrer">https://github.com/ialidzhikov/gardener-extension-registry-cache/tree/enh/extension-resources</a></li></ul><h2 id="ğŸ§ª-testmachinery-code-duplication-topic" tabindex="-1">ğŸ§ª TestMachinery Code Duplication Topic <a class="header-anchor" href="#ğŸ§ª-testmachinery-code-duplication-topic" aria-label="Permalink to &quot;ğŸ§ª TestMachinery Code Duplication Topic&quot;">â€‹</a></h2><p><strong>Problem Statement:</strong> Right now, in the code base we have different codebase for e2e tests and TM tests. Theoretically, it should be possible to have a single codebase for a test and be able to executed it against a local setup (e2e tests) and against a real landscape (TM test). Let&#39;s check if we could consolidate the codebases for e2e tests and TM tests into a single one.</p><p><strong>Motivation/Benefits:</strong> Improved developer experience, unified testing framework, unified integration (e2e or TM) test layout</p><p><strong>Achievements:</strong> We managed to adapt one simple test - the Shoot hibernation test (<a href="https://github.com/gardener/gardener/blob/v1.123.0/test/testmachinery/system/shoot_hibernation/hibernation_test.go" target="_blank" rel="noreferrer"><code>test/testmachinery/system/shoot_hibernation</code></a>). We adapted the test to use the <code>gardener.ShootFramework</code> instead of <code>framework.ShootFramework</code>.</p><p><strong>Next Steps:</strong></p><ul><li>Adapt a more complex TM test.</li><li>Open umbrella issue for the story.</li><li>Raise a PR for the hibernation test adaptations.</li></ul><p><strong>Issue:</strong><a href="https://github.com/gardener/gardener/issues/12610" target="_blank" rel="noreferrer">https://github.com/gardener/gardener/issues/12610</a></p><p><strong>Code/Pull Requests:</strong><a href="https://github.com/ialidzhikov/gardener/tree/enh/tm-code-duplication" target="_blank" rel="noreferrer">https://github.com/ialidzhikov/gardener/tree/enh/tm-code-duplication</a></p><h2 id="ğŸŒ-multiple-internal-domain-secrets-selector-based-assignment-to-seeds" tabindex="-1">ğŸŒ Multiple Internal Domain Secrets: Selector-based assignment to <code>Seed</code>s <a class="header-anchor" href="#ğŸŒ-multiple-internal-domain-secrets-selector-based-assignment-to-seeds" aria-label="Permalink to &quot;ğŸŒ Multiple Internal Domain Secrets: Selector-based assignment to `Seed`s&quot;">â€‹</a></h2><p><strong>Problem Statement:</strong> Introduce a label-selector mechanism for Internal Domain Secrets, allowing them to dynamically match <code>Seed</code>s based on labels. This helps automate and scale domain assignment in multi-<code>Seed</code> environments.</p><p><strong>Motivation/Benefits:</strong> ğŸ“‰ Reduce risks of DNS rate limits by using per-<code>Seed</code> DNS internal domain credentials ğŸ”’ Improved security by allowing credentials with reduced permission scope to be provided ğŸ’» Explicit API for internal domain credentials over implicit API based on labels and annotations</p><p><strong>Achievements:</strong> We made API extension proposal for the Seed resource. A PoC has been implemented.</p><p><strong>Next Steps:</strong></p><ul><li>The PoC changes have to be made PR ready - add tests, documentation.</li><li>Consider applying similar changes to the default domain secret</li></ul><p><strong>Issue:</strong><a href="https://github.com/gardener/gardener/issues/12596" target="_blank" rel="noreferrer">https://github.com/gardener/gardener/issues/12596</a></p><p><strong>Code/Pull Requests:</strong><a href="https://github.com/dimityrmirchev/gardener/tree/mult-internal-dns" target="_blank" rel="noreferrer">https://github.com/dimityrmirchev/gardener/tree/mult-internal-dns</a></p><h2 id="ğŸ“Š-project-level-resourcequota-enforcement-issue-10878" tabindex="-1">ğŸ“Š <code>Project</code>-level ResourceQuota enforcement (Issue <a href="https://github.com/gardener/gardener/issues/10878" target="_blank" rel="noreferrer">#10878</a>) <a class="header-anchor" href="#ğŸ“Š-project-level-resourcequota-enforcement-issue-10878" aria-label="Permalink to &quot;ğŸ“Š `Project`-level ResourceQuota enforcement (Issue [#10878](https://github.com/gardener/gardener/issues/10878))&quot;">â€‹</a></h2><p><strong>Problem Statement:</strong> Gardener lacks proper <code>ResourceQuota</code> enforcement for <code>Project</code> resources. This task aims to define quota rules per project (e.g., Shoot limits) and integrate them into admission or controller layers to prevent resource overconsumption.</p><p><strong>Motivation/Benefits:</strong> ğŸ™… Prevent rough actor that endlessly creates <code>Project</code>s until ETCD is filled up</p><p><strong>Achievements:</strong> We made API proposal in the <a href="https://github.com/gardener/gardener/issues/10878" target="_blank" rel="noreferrer">Add quota for <code>project</code> resources</a> issue. PoC has been implemented based on the proposal. The API was implemented but the validation has not been yet implemented.</p><p><strong>Next Steps:</strong></p><ul><li>Discuss at TSC.</li><li>Gather feedback on the proposal in the issue.</li></ul><p><strong>Issue:</strong><a href="https://github.com/gardener/gardener/issues/10878" target="_blank" rel="noreferrer">https://github.com/gardener/gardener/issues/10878</a></p><p><strong>Code/Pull Requests:</strong></p><p><img src="https://apeirora.eu/assets/img/BMWK-EU.png" alt="ApeiroRA"></p>',78)]))}const m=t(s,[["render",i]]);export{p as __pageData,m as default};
