import{_ as t,c as a,o,a2 as r}from"./chunks/framework.Bfq10Vlj.js";const s="/assets/01-add-service-account.CLyum0An.png",c="/assets/02-enter-service-account-details.Cj-pf_7J.png",n="/assets/03-download-service-account-kubeconfig.BrLG48J7.png",i="/assets/04-delete-service-account.G9J0E7S8.png",b=JSON.parse('{"title":"Automating Project Resource Management","description":"","frontmatter":{"aliases":["/docs/dashboard/gardener-api/"],"github_repo":"https://github.com/gardener/dashboard","github_subdir":"docs/usage","params":{"github_branch":"master"},"path_base_for_github_subdir":{"from":"content/docs/dashboard/automated-resource-management.md","to":"automated-resource-management.md"},"persona":"Users","title":"Automating Project Resource Management","prev":false,"next":false},"headers":[],"relativePath":"docs/dashboard/automated-resource-management/index.md","filePath":"docs/dashboard/automated-resource-management.md","lastUpdated":null}'),d={name:"docs/dashboard/automated-resource-management/index.md"};function l(u,e,m,h,p,g){return o(),a("div",null,e[0]||(e[0]=[r('<h1 id="automating-project-resource-management" tabindex="-1">Automating Project Resource Management <a class="header-anchor" href="#automating-project-resource-management" aria-label="Permalink to &quot;Automating Project Resource Management&quot;">​</a></h1><h2 id="overview" tabindex="-1">Overview <a class="header-anchor" href="#overview" aria-label="Permalink to &quot;Overview&quot;">​</a></h2><p>The project resource operations that are performed manually in the dashboard or via <code>kubectl</code> can be automated using the <a href="/docs/gardener/api-reference/"><strong>Gardener API</strong></a> and a <strong>Service Account</strong> authorized to perform them.</p><h2 id="create-a-service-account" tabindex="-1">Create a Service Account <a class="header-anchor" href="#create-a-service-account" aria-label="Permalink to &quot;Create a Service Account&quot;">​</a></h2><h3 id="prerequisites" tabindex="-1">Prerequisites <a class="header-anchor" href="#prerequisites" aria-label="Permalink to &quot;Prerequisites&quot;">​</a></h3><ul><li>You are logged on to the Gardener Dashboard</li><li>You have <a href="/docs/dashboard/working-with-projects/">created a project</a></li></ul><h3 id="steps" tabindex="-1">Steps <a class="header-anchor" href="#steps" aria-label="Permalink to &quot;Steps&quot;">​</a></h3><ol><li><p>Select your project and choose <em>MEMBERS</em> from the menu on the left.</p></li><li><p>Locate the section <em>Service Accounts</em> and choose <em>+</em>.</p><p><img src="'+s+'" alt="Add service account"></p></li><li><p>Enter the service account details.</p><p><img src="'+c+'" alt="Enter service account details"></p><p>The following <em>Roles</em> are available:</p></li></ol><table tabindex="0"><thead><tr><th style="text-align:left;">Role</th><th style="text-align:left;">Granted Permissions</th></tr></thead><tbody><tr><td style="text-align:left;"><em>Owner</em></td><td style="text-align:left;">Combines the <em>Admin</em>, <em>UAM</em> and <em>Service Account Manager</em> roles. There can only be one owner per project. You can change the owner on the project administration page.</td></tr><tr><td style="text-align:left;"><em>Admin</em></td><td style="text-align:left;">Allows to manage resources inside the project (e.g. secrets, shoots, configmaps and similar) and to manage permissions for service accounts. Note that the <em>Admin</em> role has read-only access to service accounts.</td></tr><tr><td style="text-align:left;"><em>Viewer</em></td><td style="text-align:left;">Provides read access to project details and shoots. Has access to shoots but is not able to create new ones. Cannot read cloud provider secrets.</td></tr><tr><td style="text-align:left;"><em>UAM</em></td><td style="text-align:left;">Allows to add/modify/remove human users, service accounts or groups to/from the project member list. In case an external UAM system is connected via a service account, only this account should get the <em>UAM</em> role.</td></tr><tr><td style="text-align:left;"><em><a href="/docs/gardener/project/service-account-manager/">Service Account Manager</a></em></td><td style="text-align:left;">Allows to manage service accounts inside the project namespace and request tokens for them. The permissions of the created service accounts are instead managed by the <em>Admin</em> role. For security reasons this role should not be assigned to service accounts. In particular it should be ensured that the service account is not able to refresh service account tokens forever.</td></tr></tbody></table><ol><li>Choose <em>CREATE</em>.</li></ol><h2 id="use-the-service-account" tabindex="-1">Use the Service Account <a class="header-anchor" href="#use-the-service-account" aria-label="Permalink to &quot;Use the Service Account&quot;">​</a></h2><p>To use the service account, download or copy its <code>kubeconfig</code>. With it you can connect to the API endpoint of your Gardener project.</p><p><img src="'+n+'" alt="Download service account kubeconfig"></p><blockquote><p>Note: The downloaded <code>kubeconfig</code> contains the service account credentials. Treat with care.</p></blockquote><h2 id="delete-the-service-account" tabindex="-1">Delete the Service Account <a class="header-anchor" href="#delete-the-service-account" aria-label="Permalink to &quot;Delete the Service Account&quot;">​</a></h2><p>Choose <em>Delete Service Account</em> to delete it.</p><p><img src="'+i+'" alt="Delete service account"></p><h2 id="related-links" tabindex="-1">Related Links <a class="header-anchor" href="#related-links" aria-label="Permalink to &quot;Related Links&quot;">​</a></h2><ul><li><a href="/docs/gardener/project/service-account-manager/">Service Account Manager</a></li></ul>',19)]))}const f=t(d,[["render",l]]);export{b as __pageData,f as default};
