import{_ as r,c as t,o as a,a2 as n}from"./chunks/framework.Bfq10Vlj.js";const g=JSON.parse('{"title":"OpenID Connect Services","description":"Gardener extension controller for OpenID Connect services for shoot clusters","frontmatter":{"description":"Gardener extension controller for OpenID Connect services for shoot clusters","github_repo":"https://github.com/gardener/gardener-extension-shoot-oidc-service","github_subdir":".","params":{"github_branch":"master"},"path_base_for_github_subdir":{"from":"content/docs/extensions/others/gardener-extension-shoot-oidc-service/_index.md","to":"README.md"},"title":"OpenID Connect Services","prev":false,"next":false},"headers":[],"relativePath":"docs/extensions/others/gardener-extension-shoot-oidc-service/_index.md","filePath":"docs/extensions/others/gardener-extension-shoot-oidc-service/_index.md","lastUpdated":null}'),o={name:"docs/extensions/others/gardener-extension-shoot-oidc-service/_index.md"};function s(i,e,l,d,c,h){return a(),t("div",null,e[0]||(e[0]=[n(`<h1 id="gardener-extension-for-openid-connect-services" tabindex="-1"><a href="https://gardener.cloud" target="_blank" rel="noreferrer">Gardener Extension for openid connect services</a> <a class="header-anchor" href="#gardener-extension-for-openid-connect-services" aria-label="Permalink to &quot;[Gardener Extension for openid connect services](https://gardener.cloud)&quot;">​</a></h1><p><a href="https://api.reuse.software/info/github.com/gardener/gardener-extension-shoot-oidc-service" target="_blank" rel="noreferrer"><img src="https://api.reuse.software/badge/github.com/gardener/gardener-extension-shoot-oidc-service" alt="REUSE status"></a><a href="https://github.com/gardener/gardener-extension-shoot-oidc-service/actions/workflows/non-release.yaml" target="_blank" rel="noreferrer"><img src="https://github.com/gardener/gardener-extension-shoot-oidc-service/raw/workflows/non-release.yaml/badge.svg" alt="Build"></a><a href="https://goreportcard.com/report/github.com/gardener/gardener-extension-shoot-oidc-service" target="_blank" rel="noreferrer"><img src="https://goreportcard.com/badge/github.com/gardener/gardener-extension-shoot-oidc-service" alt="Go Report Card"></a></p><p>Project Gardener implements the automated management and operation of <a href="https://kubernetes.io/" target="_blank" rel="noreferrer">Kubernetes</a> clusters as a service. Its main principle is to leverage Kubernetes concepts for all of its tasks.</p><p>Recently, most of the vendor specific logic has been developed <a href="https://github.com/gardener/gardener" target="_blank" rel="noreferrer">in-tree</a>. However, the project has grown to a size where it is very hard to extend, maintain, and test. With <a href="https://github.com/gardener/gardener/blob/master/docs/proposals/01-extensibility.md" target="_blank" rel="noreferrer">GEP-1</a> we have proposed how the architecture can be changed in a way to support external controllers that contain their very own vendor specifics. This way, we can keep Gardener core clean and independent.</p><p>This controller implements Gardener&#39;s extension contract for the <code>shoot-oidc-service</code> extension.</p><p>An example for a <code>ControllerRegistration</code> resource that can be used to register this controller to Gardener can be found <a href="https://github.com/gardener/gardener-extension-shoot-oidc-service/blob/master/example/controller-registration.yaml" target="_blank" rel="noreferrer">here</a>.</p><p>Please find more information regarding the extensibility concepts and a detailed proposal <a href="https://github.com/gardener/gardener/blob/master/docs/proposals/01-extensibility.md" target="_blank" rel="noreferrer">here</a>.</p><h2 id="compatibility" tabindex="-1">Compatibility <a class="header-anchor" href="#compatibility" aria-label="Permalink to &quot;Compatibility&quot;">​</a></h2><p>The following lists compatibility requirements of this extension controller with regards to other Gardener components.</p><table tabindex="0"><thead><tr><th>OIDC Extension</th><th>Gardener</th><th>Notes</th></tr></thead><tbody><tr><td><code>== v0.15.0</code></td><td><code>&gt;= 1.60.0 &lt;= v1.64.0</code></td><td>A typical side-effect when running Gardener &lt; v1.63.0 is an unexpected scale-down of the OIDC webhook from <code>2 -&gt; 1</code>.</td></tr><tr><td><code>== v0.16.0</code></td><td><code>&gt;= 1.65.0</code></td><td></td></tr></tbody></table><hr><h2 id="extension-resources" tabindex="-1">Extension Resources <a class="header-anchor" href="#extension-resources" aria-label="Permalink to &quot;Extension Resources&quot;">​</a></h2><p>Example extension resource:</p><div class="language-yaml vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang">yaml</span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span style="--shiki-light:#22863A;--shiki-dark:#85E89D;">apiVersion</span><span style="--shiki-light:#24292E;--shiki-dark:#E1E4E8;">: </span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;">extensions.gardener.cloud/v1alpha1</span></span>
<span class="line"><span style="--shiki-light:#22863A;--shiki-dark:#85E89D;">kind</span><span style="--shiki-light:#24292E;--shiki-dark:#E1E4E8;">: </span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;">Extension</span></span>
<span class="line"><span style="--shiki-light:#22863A;--shiki-dark:#85E89D;">metadata</span><span style="--shiki-light:#24292E;--shiki-dark:#E1E4E8;">:</span></span>
<span class="line"><span style="--shiki-light:#22863A;--shiki-dark:#85E89D;">  name</span><span style="--shiki-light:#24292E;--shiki-dark:#E1E4E8;">: </span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;">extension-shoot-oidc-service</span></span>
<span class="line"><span style="--shiki-light:#22863A;--shiki-dark:#85E89D;">  namespace</span><span style="--shiki-light:#24292E;--shiki-dark:#E1E4E8;">: </span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;">shoot--project--abc</span></span>
<span class="line"><span style="--shiki-light:#22863A;--shiki-dark:#85E89D;">spec</span><span style="--shiki-light:#24292E;--shiki-dark:#E1E4E8;">:</span></span>
<span class="line"><span style="--shiki-light:#22863A;--shiki-dark:#85E89D;">  type</span><span style="--shiki-light:#24292E;--shiki-dark:#E1E4E8;">: </span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;">shoot-oidc-service</span></span></code></pre></div><p>When an extension resource is reconciled, the extension controller will create an instance of <a href="https://github.com/gardener/oidc-webhook-authenticator" target="_blank" rel="noreferrer">OIDC Webhook Authenticator</a>. These resources are placed inside the shoot namespace on the seed. Also, the controller takes care about generating necessary <code>RBAC</code> resources for the seed as well as for the shoot.</p><p>Please note, this extension controller relies on the <a href="/docs/gardener/concepts/resource-manager/">Gardener-Resource-Manager</a> to deploy k8s resources to seed and shoot clusters.</p><h2 id="how-to-start-using-or-developing-this-extension-controller-locally" tabindex="-1">How to start using or developing this extension controller locally <a class="header-anchor" href="#how-to-start-using-or-developing-this-extension-controller-locally" aria-label="Permalink to &quot;How to start using or developing this extension controller locally&quot;">​</a></h2><p>You can run the controller locally on your machine by executing <code>make start</code>.</p><p>We are using Go modules for Golang package dependency management and <a href="https://github.com/onsi/ginkgo" target="_blank" rel="noreferrer">Ginkgo</a>/<a href="https://github.com/onsi/gomega" target="_blank" rel="noreferrer">Gomega</a> for testing.</p><h2 id="feedback-and-support" tabindex="-1">Feedback and Support <a class="header-anchor" href="#feedback-and-support" aria-label="Permalink to &quot;Feedback and Support&quot;">​</a></h2><p>Feedback and contributions are always welcome!</p><p>Please report bugs or suggestions as <a href="https://github.com/gardener/gardener-extension-shoot-oidc-service/issues" target="_blank" rel="noreferrer">GitHub issues</a> or reach out on <a href="https://gardener-cloud.slack.com/" target="_blank" rel="noreferrer">Slack</a> (join the workspace <a href="https://gardener.cloud/community/community-bio/" target="_blank" rel="noreferrer">here</a>).</p><h2 id="learn-more" tabindex="-1">Learn more! <a class="header-anchor" href="#learn-more" aria-label="Permalink to &quot;Learn more!&quot;">​</a></h2><p>Please find further resources about out project here:</p><ul><li><a href="https://gardener.cloud/" target="_blank" rel="noreferrer">Our landing page gardener.cloud</a></li><li><a href="https://kubernetes.io/blog/2018/05/17/gardener/" target="_blank" rel="noreferrer">&quot;Gardener, the Kubernetes Botanist&quot; blog on kubernetes.io</a></li><li><a href="https://kubernetes.io/blog/2019/12/02/gardener-project-update/" target="_blank" rel="noreferrer">&quot;Gardener Project Update&quot; blog on kubernetes.io</a></li><li><a href="https://github.com/gardener/gardener/blob/master/docs/proposals/01-extensibility.md" target="_blank" rel="noreferrer">GEP-1 (Gardener Enhancement Proposal) on extensibility</a></li><li><a href="https://github.com/gardener/gardener/tree/master/docs/extensions" target="_blank" rel="noreferrer">Extensibility API documentation</a></li><li><a href="https://godoc.org/github.com/gardener/gardener/extensions/pkg" target="_blank" rel="noreferrer">Gardener Extensions Golang library</a></li><li><a href="/docs/gardener/api-reference/">Gardener API Reference</a></li></ul>`,25)]))}const u=r(o,[["render",s]]);export{g as __pageData,u as default};
