import{_ as r}from"./chunks/etcd-druid-with-tagline.DcOZiePE.js";import{_ as t,c as a,o,a2 as d}from"./chunks/framework.Bfq10Vlj.js";const b=JSON.parse('{"title":"etcd-druid","description":"A druid for etcd management in Gardener","frontmatter":{"description":"A druid for etcd management in Gardener","github_repo":"https://github.com/gardener/etcd-druid","github_subdir":".","params":{"github_branch":"master"},"path_base_for_github_subdir":{"from":"content/docs/other-components/etcd-druid/_index.md","to":"README.md"},"title":"etcd-druid","prev":false,"next":false},"headers":[],"relativePath":"docs/other-components/etcd-druid/_index.md","filePath":"docs/other-components/etcd-druid/_index.md","lastUpdated":null}'),i={name:"docs/other-components/etcd-druid/_index.md"};function n(s,e,c,l,u,h){return o(),a("div",null,e[0]||(e[0]=[d('<p><img src="'+r+'" style="width:120%;"></p><p><a href="https://api.reuse.software/info/github.com/gardener/etcd-druid" target="_blank" rel="noreferrer"><img src="https://api.reuse.software/badge/github.com/gardener/etcd-druid" alt="REUSE status"></a><a href="https://github.com/gardener/etcd-druid/actions/workflows/non-release.yaml" target="_blank" rel="noreferrer"><img src="https://github.com/gardener/etcd-druid/raw/workflows/non-release.yaml/badge.svg" alt="Build"></a><a href="https://goreportcard.com/report/github.com/gardener/etcd-druid" target="_blank" rel="noreferrer"><img src="https://goreportcard.com/badge/github.com/gardener/etcd-druid" alt="Go Report Card"></a><a href="https://github.com/gardener/etcd-druid/blob/master/LICENSE" target="_blank" rel="noreferrer"><img src="https://img.shields.io/badge/License-Apache--2.0-blue.svg" alt="License: Apache-2.0"></a><a href="https://github.com/gardener/etcd-druid" target="_blank" rel="noreferrer"><img src="https://img.shields.io/github/v/release/gardener/etcd-druid.svg?style=flat" alt="Release"></a><a href="https://pkg.go.dev/github.com/gardener/etcd-druid" target="_blank" rel="noreferrer"><img src="https://pkg.go.dev/badge/github.com/gardener/etcd-druid.svg" alt="Go Reference"></a><a href="https://gardener.github.io/etcd-druid/index.html" target="_blank" rel="noreferrer"><img src="https://img.shields.io/badge/Docs-reference-orange" alt="Docs"></a></p><p><code>etcd-druid</code> is an <a href="https://github.com/etcd-io/etcd" target="_blank" rel="noreferrer">etcd</a> <a href="https://kubernetes.io/docs/concepts/extend-kubernetes/operator/" target="_blank" rel="noreferrer">operator</a> which makes it easy to configure, provision, reconcile, monitor and delete etcd clusters. It enables management of etcd clusters through <a href="https://github.com/gardener/etcd-druid/blob/master/api/core/v1alpha1/crds/druid.gardener.cloud_etcds.yaml" target="_blank" rel="noreferrer">declarative Kubernetes API model</a>.</p><p>In every etcd cluster managed by <code>etcd-druid</code>, each etcd member is a two container <code>Pod</code> which consists of:</p><ul><li><a href="https://github.com/gardener/etcd-wrapper" target="_blank" rel="noreferrer">etcd-wrapper</a> which manages the lifecycle (validation &amp; initialization) of an etcd.</li><li><a href="https://github.com/gardener/etcd-backup-restore" target="_blank" rel="noreferrer">etcd-backup-restore</a> sidecar which currently provides the following capabilities (the list is not comprehensive): <ul><li><a href="https://github.com/etcd-io/etcd" target="_blank" rel="noreferrer">etcd</a> DB validation.</li><li>Scheduled <a href="https://github.com/etcd-io/etcd" target="_blank" rel="noreferrer">etcd</a> DB defragmentation.</li><li>Backup - <a href="https://github.com/etcd-io/etcd" target="_blank" rel="noreferrer">etcd</a> DB snapshots are taken regularly and backed in an object store if one is configured.</li><li>Restoration - In case of a DB corruption for a single-member cluster it helps in restoring from latest set of snapshots (full &amp; delta).</li><li>Member control operations.</li></ul></li></ul><p><code>etcd-druid</code> additionally provides the following capabilities:</p><ul><li>Facilitates declarative scale-out of <a href="https://github.com/etcd-io/etcd" target="_blank" rel="noreferrer">etcd</a> clusters.</li><li>Provides protection against accidental deletion/mutation of resources provisioned as part of an etcd cluster.</li><li>Offers an asynchronous and threshold based capability to process backed up snapshots to: <ul><li>Potentially minimize the recovery time by leveraging restoration from backups followed by <a href="https://etcd.io/docs/v3.4/op-guide/maintenance/" target="_blank" rel="noreferrer">etcd&#39;s compaction and defragmentation</a>.</li><li>Indirectly assert integrity of the backed up snaphots.</li></ul></li><li>Allows seamless copy of backups between any two object store buckets.</li></ul><h2 id="start-using-or-developing-etcd-druid-locally" tabindex="-1">Start using or developing <code>etcd-druid</code> locally <a class="header-anchor" href="#start-using-or-developing-etcd-druid-locally" aria-label="Permalink to &quot;Start using or developing `etcd-druid` locally&quot;">​</a></h2><p>If you are looking to try out druid then you can use a <a href="https://kind.sigs.k8s.io/" target="_blank" rel="noreferrer">Kind</a> cluster based setup.</p><p><a href="https://github.com/user-attachments/assets/cfe0d891-f709-4d7f-b975-4300c6de67e4" target="_blank" rel="noreferrer">https://github.com/user-attachments/assets/cfe0d891-f709-4d7f-b975-4300c6de67e4</a></p><p>For detailed documentation, see our <a href="https://gardener.github.io/etcd-druid/index.html" target="_blank" rel="noreferrer">docs</a>.</p><h2 id="contributions" tabindex="-1">Contributions <a class="header-anchor" href="#contributions" aria-label="Permalink to &quot;Contributions&quot;">​</a></h2><p>If you wish to contribute then please see our <a href="/docs/other-components/etcd-druid/contribution/">contributor guidelines</a>.</p><h2 id="feedback-and-support" tabindex="-1">Feedback and Support <a class="header-anchor" href="#feedback-and-support" aria-label="Permalink to &quot;Feedback and Support&quot;">​</a></h2><p>We always look forward to active community engagement. Please report bugs or suggestions on how we can enhance <code>etcd-druid</code> on <a href="https://github.com/gardener/etcd-druid/issues" target="_blank" rel="noreferrer">GitHub Issues</a>.</p><h2 id="license" tabindex="-1">License <a class="header-anchor" href="#license" aria-label="Permalink to &quot;License&quot;">​</a></h2><p>Release under <a href="https://github.com/gardener/etcd-druid/blob/master/LICENSE" target="_blank" rel="noreferrer">Apache-2.0</a> license.</p>',17)]))}const f=t(i,[["render",n]]);export{b as __pageData,f as default};
